@import url("https://fonts.googleapis.com/css2?family=Libre+Baskerville:ital@1&family=Playfair+Display:ital,wght@0,500;0,900;1,700&family=Raleway&display=swap");
//MEDIA QUERIES
$max-s: 480px;
$max-m: 600px;
$max-l: 1025px;
//COLOURS
$primary: #343539;
$secondary: #ac9b8c;
$blush: #d9d2cd;
$blue-gray: #6e6e76;
$light-gray: #f5f5f5;
$dark-grey: #c8cbd1;
$text: #343539;
$white: #ffffff;
$black: #000000;
//TYPOGRAPHY
$serif: "Playfair Display",
serif !default;
$sans-serif: "Raleway",
sans-serif !default;
$quote: "Raleway",
sans-serif;
$italic: italic;
$regular: 500;
$bold: 700;
$extra-bold: 900;
$center: center;
$left: left;
$justify: justify;
$border: 2px solid $secondary;
$font-l: 3rem;
$font-m: 2rem;
$font-s: 1.6rem;
$font-xs: 1.4rem;
//PADDING & MARGIN
$padding-xl: 2rem;
$padding-l: 1.5rem;
$padding-m: 1rem;
$padding-s: 0.5rem;
$padding-xs: 0.2rem;
$padding-quote: 0.5rem 0 0 2rem;
$margin-quote: 0.5rem 0 0 2rem;
$margin-send: 0.4rem 0rem 0 1rem;
//WIDTH & HEIGHT
$max-width: 100%;

sources.styles/_base.scss:
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
    font-family: $serif;
}

//MIXIN
@mixin main-text {
    color: $text;
    letter-spacing: 0.1rem;
    text-align: $center;
}

@mixin paragraph-text {
    color: $blue-gray;
    font-family: $serif;
    font-size: $font-s;
}

@mixin quote-text {
    color: $blue-gray;
    font-family: $quote;
    font-size: $font-xs;
    text-align: $center;
    font-style: italic;
}

@mixin input-text {
    color: $secondary;
    font-family: $sans-serif;
    font-size: $font-s;
}

@mixin flex {
    display: flex;
}

//TYPOGRAPHY
h1 {
    @include main-text;
    font-weight: $extra-bold;
    font-size: $font-l;
}

h2 {
    @include main-text;
    font-weight: $bold;
    font-size: $font-m;
}

h3 {
    @include main-text;
    text-align: $left;
    font-weight: normal;
}

h4 {
    @include quote-text;
}

h6 {
    @include input-text;
}

p {
    @include paragraph-text;
}

.link {
    @include paragraph-text;
    color: $secondary;
    &-button {
        @include paragraph-text;
        font-size: 1rem;
        text-decoration: none;
        border: $border;
        padding: 0.5em 1rem;
        width: fit-content;
        cursor: pointer;
        &:hover {
            background: $blush;
        }
        & a {
            all: unset;
            color: $primary;
        }
    }
}

