sources.styles/_base.scss:
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
    font-family: $serif;
}

li {
    list-style: none;
}

//MIXIN
@mixin main-text {
    color: $text;
    letter-spacing: 0.1rem;
    text-align: $center;
}

@mixin paragraph-text {
    color: $text;
    font-family: $sans-serif;
    font-size: $font-s;
    line-height: 2rem;
    padding-bottom: 1rem;
    font-weight: 200;
}

@mixin quote-text {
    color: $blue-gray;
    font-family: $quote;
    font-size: $font-xs;
    text-align: $center;
    font-style: italic;
    font-weight: 100;
}

@mixin input-text {
    color: $secondary;
    font-family: $sans-serif;
    font-size: $font-s;
}

@mixin categories {
    font-family: $quote;
    font-size: 1rem;
    color: $text;
    text-decoration: none;
    text-align: center;
    &:hover {
        color: $blush;
    }
}

@mixin flex {
    display: flex;
}

@mixin flex-column {
    display: flex;
    flex-direction: column;
    align-items: center;
}

@mixin padding-xl {
    padding: $padding-xl;
    @media (max-width: $max-m) {
        padding: $padding-l;
    }
}

@mixin grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, 22.8rem);
    grid-column-gap: 1rem;
    grid-row-gap: 4rem;
    justify-content: center;
}

//TYPOGRAPHY
h1 {
    @include main-text;
    font-weight: $extra-bold;
    font-size: $font-l;
    padding-bottom: 1rem;
}

h2 {
    @include main-text;
    font-weight: $bold;
    font-size: $font-m;
    padding-bottom: 1rem;
}

h3 {
    @include main-text;
    text-align: $left;
    font-weight: normal;
    letter-spacing: 0.05rem;
}

h4 {
    @include quote-text;
    padding-bottom: 3rem;
}

h5 {
    @include main-text;
    font-weight: normal;
    font-size: $font-s;
    padding: 1.5rem 0;
    border: $border-black;
    position: absolute;
    bottom: 0;
    width: 100%;
    background-color: $white;
    &:hover {
        color: $blush;
    }
}

h6 {
    @include input-text;
}

p {
    @include paragraph-text;
}

.highlight {
    color: $blush !important;
}

.link {
    @include paragraph-text;
    color: $secondary;
    &-button {
        @include paragraph-text;
        font-size: 1rem;
        text-decoration: none;
        border: $border;
        padding: 0.5em 1rem;
        width: fit-content;
        cursor: pointer;
        &:hover {
            background: $blush;
        }
        & a {
            all: unset;
            color: $primary;
        }
    }
}

.page-heading {
    @include main-text;
    letter-spacing: 0.05rem;
    font-weight: bold;
    font-size: 2.6rem;
    padding: 1rem 0 0.2rem;
}

.subheading {
    @include paragraph-text;
