//CHECKBOX
.checkbox {
    position: relative;
    line-height: 0;
}

.checkbox:after {
    content: "";
    width: 100%;
    display: inline-block;
}

.checkbox input[type="checkbox"] {
    opacity: 0;
    width: 100%;
    height: 100%;
    position: absolute;
    cursor: pointer;
    border-color: #232323;
    border-radius: 25%;
}

.checkbox input[type="checkbox"]:focus {
    outline: 0;
}

.checkbox input[type="checkbox"]:checked+.check-container .check-off {
    border-color: green;
}

.checkbox input[type="checkbox"]:checked+.check-container .check-on:after {
    content: "✓";
    color: green;
    position: absolute;
    font-size: 1em;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

.checkbox input[type="checkbox"]+.check-container {
    width: auto;
    position: relative;
    display: inline-block;
    width: 29px;
    height: 29px;
    top: 2px;
    pointer-events: none;
}

.checkbox input[type="checkbox"]+.check-container .check-off {
    position: absolute;
    width: 25px;
    height: 25px;
    left: 0;
    top: 0;
    border-radius: 2px;
    border: 2px solid #232323;
    border-radius: 50%;
