        background-image: url(/img/redzepi.png);
        background-position: center 65%;
    }
    .h3-pr-masterchef {
        padding: 8px 0;
    }
    .h3-pr-noma {
        padding: 6px 0;
    }
}

.section-hermann {
    p {
        text-align: center;
        padding-bottom: $padding-l;
    }
    .link-button {
        margin: auto;
    }
    .hermann-video {
        margin-top: $padding-l;
        background-position: center;
        height: 50vh;
        width: 100%;
        background-size: cover;
        background-repeat: no-repeat;
        background-image: url(/img/hermann-video.png);
    }
}

@media (min-width: $max-s) {}

@media (min-width: $max-m) {
    .popular-restaurants,
    .section-articles,
    .mid-section,
    .section-hermann {
        max-width: 65vw;
        margin: auto;
    }
    .section-hermann {
        padding-top: $padding-l;
    }
    .mid-section img {
        height: 35vh;
    }
}

@media (min-width: 768px) {
    .section-hermann .hermann-video {
        height: 55vh;
    }
    .pr {
        height: 39vh;
        width: 100%;
        margin-bottom: 2rem;
    }
    .pr .pr-img {
        width: 100%;
        height: 30vh;
        background-repeat: no-repeat;
    }
    .popular-restaurants,
    .section-articles,
    .mid-section,
    .section-hermann {
        max-width: 55vw;
        margin: auto;
    }
    .pr .h3-pr {
        height: unset;
    }
    .pr .h3-pr {
        padding: 27px 0;
    }
}

@media (min-width: $max-l) {
    .popular-restaurants,
    .section-articles,
    .mid-section {
        display: grid;
        grid-template-columns: repeat(3, 1fr);
        grid-column-gap: 1rem;
        grid-row-gap: 3rem;
        max-width: 100%;
    }
    .mid-section {
        display: flex;
        flex-direction: row-reverse;
        padding-top: $padding-xl;
    }
    .mid-section-txt {
        margin: auto;
        padding: 5.5rem;
        & span {
            font-size: 35px;
        }
        & .link-button {
            margin: 0;
        }
    }
    .mid-section-txt,
    .mid-section-img {
        width: 50%;
    }
    .mid-section-txt h2,
    .mid-section-txt p {
        text-align: left;
    }
    .mid-section img {
        height: 100%;
    }
    .pr .pr-img {
        height: 40vh;
    }
    .pr {
        height: 48vh;
    }
    body>section.section-articles>div.pr-1.pr>div.pr1-header.pr-header>h3 {
        padding: 39px 0;
    }
    .section-hermann {
        display: flex;
        flex-direction: row-reverse;
        max-width: 100%;
        padding-top: $padding-xl;
        & .hermann-video {
            width: 50%;
        }
        & .sec-hermann-txt {
            width: 50%;
            margin: auto;
            padding: 5.5rem;
        }
        & h2,
        p {
            text-align: left;
        }
        & .link-button {
            margin: 0;
        }
    }
    .section-articles {
        padding-top: $padding-xl;
    }
}

@media (min-width: 1146px) {
    .h3-pr-masterchef {
        padding: 39px 0 !important;
    }
}

@media (min-width: 1593px) {
    .h3-pr-noma {
        padding: 39px 0 !important;
    }
    .section-hermann .hermann-video {
        height: 62vh;
    }
}

sources.styles/style.scss:
@import "variables";
@import "base";
@import "restaurant-home";
@import "login";
@import "article";
@import "recipe";
@import "recipes";
@import "home-page";
.padding-main {
    @include padding-xl;
}

//BANNER
.banner {
    height: 50vh;
    background: $secondary;
    width: 100%;
    @media (max-width: $max-l) {
        height: 30vh;
    }
}

.padding-main {
    @include padding-xl;
}

.banner-img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

//SUB BANNER
.categories {
    display: flex;
    justify-content: space-around;
    margin: 0.5rem 0;
    border-color: $blue-gray;
    border-style: none none none solid;
    border-width: 1px;
    & a {
        width: 17.5%;
        @include categories;
        border-color: $blue-gray;
        border-style: none solid none none;
        border-width: 1px;
    }
}

//SORTING
.filter-sort {
    padding: 1rem 2rem;
}

.filter-sort {
    @include paragraph-text;
    font-size: $font-xs;
    display: block;
    position: relative;
}

.filter-sort-list {
    @include paragraph-text;
    font-size: $font-xs;
    padding: 0 1rem;
    & li {
        list-style: none;
        padding: 0.1rem;
    }
    & label {
        @extend .filter-sort-list;
        padding-left: 0.5rem;
    }
}

.dropdown {
    visibility: hidden;
    opacity: 0;
    background: $white;
    position: absolute;
    padding: 1rem;
    left: 2rem;
    display: none;
    border: $border-black;
    z-index: 1;
}

.filter-sort:hover>.dropdown,
.filter-sort .dropdown:hover {
    visibility: visible;
    opacity: 1;
    @include flex;
}

//MEDIA QUERIES
@media (max-width: $max-s) {
    .filter-sort {
        padding: 1rem 0.5rem;
        &-list {
            padding: 0 0.5rem;
        }
    }
    .categories {
        display: none;
    }
    .dropdown {
        padding: 0;
        left: 0.5rem;
    }
    .catalogue-grid {
        grid-row-gap: 2rem;
    }
}
